freq_sexo_rel <- datos_curso %>%
  group_by(sexo) %>%
  summarise(
    ni = n(),
    fi = ni/nrow(datos_curso)
  )


gt(freq_sexo_rel)%>%
  fmt_percent(columns = fi, 
              decimals = 1) %>%
  gt_theme_excel()
